const initTodos = [
    { id: 1, text: 'clean room', isSelected: false },
    { id: 2, text: 'do pushups', isSelected: false },
    { id: 3, text: 'do my homework', isSelected: false },
  ];

function Todo() {
    const [todos, setTodos] = useState(initTodos);

    function handleClick(id) {
      const newTodos = [...todos];
      const todo = newTodos.find((todo) => todo.id === id);
      todo.isSelected = !todo.isSelected;
      setTodos(newTodos);
    }

    const addTodo = () => {
      const todoId = todos.length === 0 ? 1 : todos[todos.length - 1].id + 1;

      setTodos([
        ...todos,
        {
          id: todoId,
          text: `Learn React ${todoId}`,
          isSelected: false,
        },
      ]);
    };

    const deleteSelectedTodo = () => {
      // const newTodos = [...todos];
      // newTodos.splice(id, 1);
      // setTodos(newTodos);
      const newTodos = todos.filter((todo) => todo.isSelected === false);
      setTodos(newTodos);
    };

    const clearTodoState = () => {
      const newTodos = todos.map((todo) => !!todo.isSelected);
      console.log(43, newTodos);
      return newTodos;
    };

    const reloadTodo = () => {
      console.log(48, initTodos);
      console.log(49, todos);
      clearTodoState();
      setTodos(initTodos);
    };

    return (
      <>
        <button onClick={addTodo}>Add Todo</button>
        <button onClick={deleteSelectedTodo}>Delete Todo</button>
        <button onClick={reloadTodo}>Reload Todo</button>

        <ul className="todoListContainer">
          {todos.map((todo) => (
            <TodoItem
              key={todo.id}
              todo={todo}
              id={todo.id}
              handleClickProp={handleClick}
            />
          ))}
        </ul>
      </>
    );
  }


export default ToDo;
