import React, {useState} from "react";
import todoList from './TodoList'

function TodoItem(props) {
    const [isSelected, setDone] = useState(props.done)
    const handleChange = () => {
        setDone(prevdone => {
            return !prevdone
        })
    }


    return (
        <li className="todoApp">
            <h3>
                *
                <span style={isSelected ? { textDecorationLine: 'line-through' } : {}}>
                    {props.description}
                </span>
                <input type="checkbox" checked={isSelected} onChange={handleChange}></input>
                <button onClick={() => {
                    props.setTodoState((prevItem) => {
                        return prevItem.filter(item => item.id !== props.id)
                    })
                }}>
                    Delete
                </button>
            </h3>
        </li>
    )
}

function ToDo() {
    const [todoState, setTodoState] = useState(todoList)

    const addTodo = () => {
        const newId = todoState.length === 0 ? 1 : todoState[todoState.length-1].id + 1
        const newTodo = { id: newId, description: "random text", isSelected: false}
        setTodoState(prevTodos => {
            return [...prevTodos, newTodo]
        })
    }

    return (
        <div>
            <button onClick={addTodo}>Add Todo</button>
            <ListTitle />
            {todoState.length === 0 ? "No items..." : (
                <ul>
                    {todoState.map(todo => {
                        return <TodoItem
                            description={todo.description}
                            isSelected={todo.isSelected}
                            key={todo.id}
                            id={todo.id}
                            setTodoState={setTodoState} />
                    })}
                </ul>)}
        </div>
    ) 
    }


export default ToDo;
